// import * as redux from "redux";
import { createStore } from "redux";

const initialState = { counter: 0, showCounter: true };
const counterReducer = (state = initialState, action) => {
  if (action.type === "inc") return { counter: state.counter + 1 };
  else if (action.type === "dec") return { counter: state.counter - 1 };
  else if (action.type === "increase") return { counter: state.counter + action.amount };
  return state; // No changes if no match
};
const store = createStore(counterReducer);

export default store;
