import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/Notification/Notification";
import { uiActions } from "./store/index";

let isInitial = true; // For blocking API request on the first load

function App() {
  const cartIsVisible = useSelector((state) => state.ui.cartIsVisible);
  const cart = useSelector((state) => state.cart);
  const notification = useSelector((state) => state.ui.notification);
  // NOTE: useSelector sets up a subscription to the redux store, meaning that upon the state being updated,
  // this component will be re-executed / re-rendered, thus the fetch() inside useEffect() will be called
  // accordingly.

  const dispatch = useDispatch();
  useEffect(() => {
    const sendCartData = async () => {
      // STEP (1): Show notification of pending action
      dispatch(
        uiActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending cart data!",
        })
      );
      try {
        // STEP (2): Make the API Call
        const response = await fetch(
          "https://react-http-7a0f7-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json",
          { method: "PUT", body: JSON.stringify(cart) }
        );
        if (!response.ok) throw new Error("Sending cart date failed");
        // const data = await response.json();
        // STEP (3): If successful, show success notification
        dispatch(
          uiActions.showNotification({
            status: "success",
            title: "Success!",
            message: "Sent cart data successfully",
          })
        );
      } catch (err) {
        // STEP (4): If there was an error, show error notification
        console.log(err);
        dispatch(uiActions.showNotification({ state: "error", title: "Error", message: err.message }));
      } finally {
        setTimeout(() => dispatch(uiActions.showNotification(null)), 3000);
      }
    };
    if (isInitial) {
      // Will block the code from executing sendCartData for the first time.
      isInitial = false;
      return;
    }
    sendCartData();
  }, [cart, dispatch]); // NOTE: While user-defined functions should be using useCallback, dispatch isn't
  // required to undergo the transformation because redux will take care of that.
  return (
    <>
      {notification && (
        <Notification status={notification.status} title={notification.title} message={notification.message} />
      )}
      <Layout>
        {cartIsVisible && <Cart />}
        <Products />
      </Layout>
    </>
  );
}

export default App;
