import { configureStore, createSlice } from "@reduxjs/toolkit";

const uiSlice = createSlice({
  name: "ui",
  initialState: { cartIsVisible: false },
  reducers: {
    toggleCart: (state) => ({ cartIsVisible: !state.cartIsVisible }),
  },
});

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
    // totalAmount: 0,
  },
  reducers: {
    addItemToCart: (state, action) => {
      const itemToAdd = action.payload.title;
      console.log(action.payload.title);

      const existingItem = state.items.find((item) => item.id === itemToAdd.id);

      if (existingItem) return { ...state, [existingItem]: state[existingItem] + 1 };
      else return { ...state, [itemToAdd]: 1 };
    },
    removeItemToCart: (state, action) => {
      const item = action.payload.title;
      if (!state[item]) return; // If there is no itemId in the state object yet
      //(ie deletion called before addition)
      const newState = { ...state, [item]: state.item - 1 };
      if (newState[item] === 0) delete newState[item];
      return newState;
    },
  },
});

const store = configureStore({
  reducer: {
    ui: uiSlice.reducer,
    // shoppingCart: cartSlice.reducer,
  },
});

export const uiActions = uiSlice.actions;
export const cartActions = cartSlice.actions;
export default store;
