import { useEffect } from "react";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useSelector, useDispatch } from "react-redux";
import { uiActions } from "./store";

function App() {
  const cartIsVisible = useSelector((state) => state.ui.cartIsVisible);
  const cart = useSelector((state) => state.cart);
  // NOTE: useSelector sets up a subscription to the redux store, meaning that upon the state being updated,
  // this component will be re-executed / re-rendered, thus the fetch() inside useEffect() will be called
  // accordingly.

  const dispatch = useDispatch();
  useEffect(() => {
    const sendCartData = async () => {
      try {
        const response = await fetch(
          "https://react-http-7a0f7-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json",
          { method: "PUT", body: JSON.stringify(cart) }
        );
        if (!response.ok) throw new Error("Sending cart date failed");
        const data = await response.json();
      } catch (err) {
        dispatch(uiActions.showNotification());
      }
    };
    sendCartData();
  }, [cart]);
  return (
    <Layout>
      {cartIsVisible && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
