import { configureStore, createSlice } from "@reduxjs/toolkit";

const shoppingCartSlice = createSlice({
  name: "shopping-cart",
  initialState: { showShoppingCart: false },
  reducers: {
    toggleCart: (state) => ({ showShoppingCart: !state.showShoppingCart }),
  },
});

const itemsSlice = createSlice({
  name: "items",
  initialState: {},
  reducers: {
    addItem: (state, action) => {
      const currentItemId = action.payload.itemId;
      if (state.hasOwnProperty[currentItemId]) return { ...state, [currentItemId]: state.currentItemId + 1 };
      else return { ...state, [currentItemId]: 1 };
    },
    removeItem: (state, action) => {
      const currentItemId = action.payload.itemId;
      if (!state[currentItemId]) return; // If there is no itemId in the state object yet
      //(ie deletion called before addition)
      const newState = { ...state, [currentItemId]: state.currentItemId - 1 };
      if (newState[currentItemId] === 0) delete newState[currentItemId];
      return newState;
    },
  },
});

const store = configureStore({
  reducer: {
    shoppingCart: shoppingCartSlice.reducer,
    items: itemsSlice.reducer,
  },
});

export const itemsAction = itemsSlice.actions;
export const shoppingCartActions = shoppingCartSlice.actions;
export default store;
