import { configureStore, createSlice } from "@reduxjs/toolkit";

const uiSlice = createSlice({
  name: "ui",
  initialState: { cartIsVisible: false },
  reducers: {
    toggleCart: (state) => ({ cartIsVisible: !state.cartIsVisible }),
  },
});

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
    totalAmount: 0,
  },
  reducers: {
    addItem: (state, action) => {
      const currentItemTitle = action.payload.title;
      console.log(action.payload.title);

      if (state[currentItemTitle]) return { ...state, [currentItemTitle]: state[currentItemTitle] + 1 };
      else return { ...state, [currentItemTitle]: 1 };
    },
    removeItem: (state, action) => {
      const currentItemTitle = action.payload.title;
      if (!state[currentItemTitle]) return; // If there is no itemId in the state object yet
      //(ie deletion called before addition)
      const newState = { ...state, [currentItemTitle]: state.currentItemTitle - 1 };
      if (newState[currentItemTitle] === 0) delete newState[currentItemTitle];
      return newState;
    },
    addItemToCartFirstTime: (state, action) => {
      const currentItemTitle = action.payload.title;
      if (state[currentItemTitle] >= 1) return;
      else return { ...state, [currentItemTitle]: 1 };
    },
  },
});

const store = configureStore({
  reducer: {
    ui: uiSlice.reducer,
    // shoppingCart: cartSlice.reducer,
  },
});

export const uiActions = uiSlice.actions;
export const cartActions = cartSlice.actions;
export default store;
